import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Navigate } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import ThoughtCabinet from './ThoughtCabinet';

const App = () => {
  const [authToken, setAuthToken] = useState(localStorage.getItem('token'));

  const ProtectedRoute = ({ component: Component, ...rest }) => (
    <Route
      {...rest}
      render={(props) =>
        authToken ? <Component {...props} /> : <Navigate to="/login" />
      }
    />
  );

  return (
    <Router>
      <Switch>
        <Route path="/register" component={Register} />
        <Route path="/login">
          <Login setAuthToken={setAuthToken} />
        </Route>
        <ProtectedRoute path="/cabinet" component={ThoughtCabinet} />
        <Navigate from="/" to="/login" />
      </Switch>
    </Router>
  );
};

export default App;
