// src/components/ThoughtEditor.js
import React from 'react';
import { useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { DndProvider } from 'react-dnd';
import './ThoughtEditor.css';

const ThoughtEditor = ({ thoughts, setThoughts, connections }) => {
  const [, drop] = useDrop(() => ({
    accept: 'thought',
    drop: (item, monitor) => {
      const delta = monitor.getDifferenceFromInitialOffset();
      const left = Math.round(item.left + delta.x);
      const top = Math.round(item.top + delta.y);
      moveThought(item.id, left, top);
    },
  }));

  const moveThought = (id, left, top) => {
    setThoughts(thoughts.map(thought => (thought.id === id ? { ...thought, left, top } : thought)));
  };

  return (
    <div ref={drop} className="thought-editor">
      {thoughts.map(thought => (
        <DraggableThought key={thought.id} thought={thought} />
      ))}
      <svg className="connection-lines">
        {connections.map((connection, index) => {
          const fromThought = thoughts.find(thought => thought.id === connection.from);
          const toThought = thoughts.find(thought => thought.id === connection.to);
          if (!fromThought || !toThought) return null;

          return (
            <line
              key={index}
              x1={fromThought.left + 50}
              y1={fromThought.top + 50}
              x2={toThought.left + 50}
              y2={toThought.top + 50}
              stroke="white"
              strokeWidth="2"
            />
          );
        })}
      </svg>
    </div>
  );
};

const DraggableThought = ({ thought }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'thought',
    item: { id: thought.id, left: thought.left, top: thought.top },
    collect: monitor => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      className="draggable-thought"
      style={{ left: thought.left, top: thought.top, opacity: isDragging ? 0.5 : 1 }}
    >
      {thought.title}
    </div>
  );
};

export default ThoughtEditor;
