// src/components/ThoughtDetails.js
import React, { useState, useEffect } from 'react';
import { FaTrash, FaEdit } from 'react-icons/fa';
import ThoughtStatus from './ThoughtStatus';
import './ThoughtDetails.css';

const ThoughtDetails = ({ thought, updateThought, cancelEditing, relatedThoughts, removeConnection, updateStatus }) => {
  const [editedThought, setEditedThought] = useState(thought);

  useEffect(() => {
    setEditedThought(thought);
  }, [thought]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedThought({ ...editedThought, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateThought(editedThought);
  };

  return (
    <div className="thought-details">
      <h2>Редактировать мысль</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="title" 
          value={editedThought.title} 
          onChange={handleChange} 
          placeholder="Название мысли" 
        />
        <textarea 
          name="details" 
          value={editedThought.details} 
          onChange={handleChange} 
          placeholder="Детали мысли"
        />
        {thought.status === 'Решение' && (
          <textarea 
            name="solution" 
            value={editedThought.solution} 
            onChange={handleChange} 
            placeholder="Решение"
          />
        )}
        <ThoughtStatus thought={thought} updateStatus={updateStatus} />
        <button type="submit"><FaEdit /> Сохранить</button>
        <button type="button" onClick={cancelEditing}><FaTrash /> Отмена</button>
      </form>
      <div className="related-thoughts">
        <h3>Связанные мысли</h3>
        <ul>
          {relatedThoughts.map(rt => (
            <li key={rt.id}>
              {rt.title}
              <button onClick={() => removeConnection(thought.id, rt.id)}><FaTrash /></button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ThoughtDetails;
