// src/components/AddThoughtForm.js
import React, { useState } from 'react';
import './AddThoughtForm.css';

const AddThoughtForm = ({ addThought }) => {
  const [newThought, setNewThought] = useState({ title: '', details: '', image: '', tags: [] });
  const [tagInput, setTagInput] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewThought({ ...newThought, [name]: value });
  };

  const handleTagChange = (e) => {
    setTagInput(e.target.value);
  };

  const addTag = () => {
    if (tagInput.trim() && !newThought.tags.includes(tagInput.trim())) {
      setNewThought({ ...newThought, tags: [...newThought.tags, tagInput.trim()] });
      setTagInput('');
    }
  };

  const removeTag = (tag) => {
    setNewThought({ ...newThought, tags: newThought.tags.filter(t => t !== tag) });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addThought(newThought);
    setNewThought({ title: '', details: '', image: '', tags: [] });
  };

  return (
    <div className="add-thought-form">
      <h2>Добавить новую мысль</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="title" 
          value={newThought.title} 
          onChange={handleChange} 
          placeholder="Название мысли" 
        />
        <textarea 
          name="details" 
          value={newThought.details} 
          onChange={handleChange} 
          placeholder="Детали мысли"
        />
        <input 
          type="text" 
          name="image" 
          value={newThought.image} 
          onChange={handleChange} 
          placeholder="URL изображения"
        />
        <div className="tags">
          <input 
            type="text" 
            value={tagInput} 
            onChange={handleTagChange} 
            placeholder="Добавить тег" 
          />
          <button type="button" onClick={addTag}>Добавить тег</button>
          <div className="tag-list">
            {newThought.tags.map(tag => (
              <span key={tag} className="tag">
                {tag}
                <button type="button" onClick={() => removeTag(tag)}>x</button>
              </span>
            ))}
          </div>
        </div>

      </form>
    </div>
  );
};

export default AddThoughtForm;
