// src/components/ThoughtDetails.js
import React, { useState, useEffect } from 'react';
import './ThoughtDetails.css';

const ThoughtDetails = ({ thought, updateThought, cancelEditing, relatedThoughts }) => {
  const [editedThought, setEditedThought] = useState(thought);
  const [tagInput, setTagInput] = useState('');

  useEffect(() => {
    setEditedThought(thought);
  }, [thought]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedThought({ ...editedThought, [name]: value });
  };

  const handleTagChange = (e) => {
    setTagInput(e.target.value);
  };

  const addTag = () => {
    if (tagInput.trim() && !editedThought.tags.includes(tagInput.trim())) {
      setEditedThought({ ...editedThought, tags: [...editedThought.tags, tagInput.trim()] });
      setTagInput('');
    }
  };

  const removeTag = (tag) => {
    setEditedThought({ ...editedThought, tags: editedThought.tags.filter(t => t !== tag) });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateThought(editedThought);
  };

  return (
    <div className="thought-details">
      <h2>Редактировать мысль</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="title" 
          value={editedThought.title} 
          onChange={handleChange} 
          placeholder="Название мысли" 
        />
        <textarea 
          name="details" 
          value={editedThought.details} 
          onChange={handleChange} 
          placeholder="Детали мысли"
        />
        <div className="tags">
          <input 
            type="text" 
            value={tagInput} 
            onChange={handleTagChange} 
            placeholder="Добавить тег" 
          />
          <button type="button" onClick={addTag}>Добавить тег</button>
          <div className="tag-list">
            {editedThought.tags.map(tag => (
              <span key={tag} className="tag">
                {tag}
                <button type="button" onClick={() => removeTag(tag)}>x</button>
              </span>
            ))}
          </div>
        </div>
        <button type="submit">Сохранить</button>
        <button type="button" onClick={cancelEditing}>Отмена</button>
        <button type="button" onClick={cancelEditing}>Назад</button>
      </form>
      <div className="related-thoughts">
        <h3>Связанные мысли</h3>
        <ul>
          {relatedThoughts.map(rt => (
            <li key={rt.id}>{rt.title}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ThoughtDetails;
