// src/components/ThoughtDetails.js
import React, { useState, useEffect } from 'react';
import { FaTrash, FaEdit, FaCheck } from 'react-icons/fa';
import './ThoughtDetails.css';

const ThoughtDetails = ({ thought, updateThought, cancelEditing, updateStatus }) => {
  const [editedThought, setEditedThought] = useState(thought);
  const [activeTab, setActiveTab] = useState('Проблема');
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    setEditedThought(thought);
  }, [thought]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedThought({ ...editedThought, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateThought(editedThought);
    setIsEditing(false);
  };

  const findSolution = () => {
    setEditedThought({ ...editedThought, status: 'Решение' });
    updateStatus(thought.id, 'Решение');
    setActiveTab('Решение');
  };

  const startEditing = () => {
    setIsEditing(true);
  };

  return (
    <div className="thought-details">
      <h2>{thought.title}</h2>
      <div className="tabs">
        <button
          className={activeTab === 'Проблема' ? 'active' : ''}
          onClick={() => setActiveTab('Проблема')}
        >
          Проблема
        </button>
        <button
          className={activeTab === 'Решение' ? 'active' : ''}
          onClick={() => setActiveTab('Решение')}
        >
          Решение
        </button>
      </div>
      <div className="thought-content">
        {activeTab === 'Проблема' && (
          isEditing ? (
            <textarea
              name="details"
              value={editedThought.details}
              onChange={handleChange}
              placeholder="Проблема"
            />
          ) : (
            <p>{thought.details}</p>
          )
        )}
        {activeTab === 'Решение' && (
          isEditing ? (
            <textarea
              name="solution"
              value={editedThought.solution}
              onChange={handleChange}
              placeholder="Решение"
            />
          ) : (
            <p>{thought.solution}</p>
          )
        )}
      </div>
      {isEditing ? (
        <>
          <button onClick={handleSubmit}><FaEdit /> Сохранить</button>
          {thought.status === 'Проблема' && (
            <button type="button" onClick={findSolution}><FaCheck /> Найти решение</button>
          )}
          <button type="button" onClick={cancelEditing}><FaTrash /> Отмена</button>
        </>
      ) : (
        <button onClick={startEditing}><FaEdit /> Редактировать</button>
      )}
    </div>
  );
};

export default ThoughtDetails;
