// src/components/ConnectionForm.js
import React, { useState } from 'react';
import { FaTrash, FaPlus } from 'react-icons/fa';
import './ConnectionForm.css';

const ConnectionForm = ({ thoughts, selectedThought, addConnection, removeConnection }) => {
  const [targetThoughtId, setTargetThoughtId] = useState('');
  const [error, setError] = useState('');

  const handleAddConnection = () => {
    const fromId = selectedThought.id;
    const toId = parseInt(targetThoughtId);

    if (thoughts.some(thought => thought.id === toId) && fromId !== toId) {
      const existingConnection = thoughts.find(thought => (thought.from === fromId && thought.to === toId) || (thought.from === toId && thought.to === fromId));
      if (!existingConnection) {
        addConnection(fromId, toId);
        setTargetThoughtId('');
        setError('');
      } else {
        setError('Связь уже существует');
      }
    } else {
      setError('Невозможно создать связь');
    }
  };

  const handleRemoveConnection = () => {
    const fromId = selectedThought.id;
    const toId = parseInt(targetThoughtId);
    removeConnection(fromId, toId);
    setTargetThoughtId('');
    setError('');
  };

  return (
    <div className="connection-form">
      <h3>Связать с другой мыслью</h3>
      <select value={targetThoughtId} onChange={(e) => setTargetThoughtId(e.target.value)}>
        <option value="" disabled>Выберите мысль</option>
        {thoughts.filter(thought => thought.id !== selectedThought.id).map(thought => (
          <option key={thought.id} value={thought.id}>{thought.title}</option>
        ))}
      </select>
      <button onClick={handleAddConnection}><FaPlus /></button>
      <button onClick={handleRemoveConnection}><FaTrash /></button>
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default ConnectionForm;
