// src/components/ThoughtTree.js
import React from 'react';
import ThoughtNode from './ThoughtNode';
import './ThoughtTree.css';

const ThoughtTree = ({ thoughts, connections, selectThought }) => {
  return (
    <div className="thought-tree">
      {thoughts.map((thought) => (
        <ThoughtNode key={thought.id} thought={thought} onSelect={selectThought} />
      ))}
      <svg className="connection-lines">
        {connections.map((connection, index) => {
          const fromThought = thoughts.find(thought => thought.id === connection.from);
          const toThought = thoughts.find(thought => thought.id === connection.to);
          if (!fromThought || !toThought) return null;

          return (
            <line
              key={index}
              x1={fromThought.left + 50}
              y1={fromThought.top + 50}
              x2={toThought.left + 50}
              y2={toThought.top + 50}
              stroke="white"
              strokeWidth="2"
            />
          );
        })}
      </svg>
    </div>
  );
};

export default ThoughtTree;
