// src/components/ThoughtDetails.js
import React, { useState } from 'react';
import './ThoughtDetails.css';

const ThoughtDetails = ({ thought, updateThought, cancelEditing, updateStatus }) => {
  const [activeTab, setActiveTab] = useState('problem');

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="thought-details">
      <div className="thought-header">
        <h1>{thought.title}</h1>
        <p className="thought-summary">
          Reveals extra special collector's edition tare bottles on the map<br />
          More money from selling tare<br />
          Learning cap for Shivers raised to 6
        </p>
      </div>

      <div className="thought-content">
        <div className="thought-tabs">
          <button 
            className={activeTab === 'problem' ? 'active' : ''} 
            onClick={() => handleTabChange('problem')}
          >
            PROBLEM
          </button>
          <button 
            className={activeTab === 'solution' ? 'active' : ''} 
            onClick={() => handleTabChange('solution')}
          >
            SOLUTION
          </button>
        </div>

        <div className="thought-text">
          {activeTab === 'problem' ? (
            <p>{thought.details}</p>
          ) : (
            <p>{thought.solution}</p>
          )}
        </div>
      </div>

      <div className="thought-actions">
        <div className="experience">
          <span>EXPERIENCE: 50/100</span><br />
          <span>SKILL POINTS: ◆◆◆</span>
        </div>
        <button className="forget-button">FORGET</button>
      </div>
    </div>
  );
};

export default ThoughtDetails;
