import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ThoughtList from './ThoughtList';
import ThoughtTree from './ThoughtTree';
import ThoughtDetails from './ThoughtDetails';
import AddThoughtForm from './AddThoughtForm';


const ThoughtCabinet = () => {
  const [thoughts, setThoughts] = useState([]);
  const [selectedThought, setSelectedThought] = useState(null);
  const [isAdding, setIsAdding] = useState(false);

  const authToken = localStorage.getItem('token');
  const userId = authToken ? JSON.parse(atob(authToken)).id : null;

  useEffect(() => {
    if (userId) {
      axios
        .get(`http://localhost:5000/thoughts?userId=${userId}`)
        .then((response) => setThoughts(response.data))
        .catch((error) => console.error('Error loading thoughts', error));
    }
  }, [userId]);

  const addThought = (thought) => {
    axios
      .post('http://localhost:5000/thoughts', { ...thought, userId })
      .then((response) => setThoughts([...thoughts, response.data]))
      .catch((error) => console.error('Error adding thought', error));
  };

  const removeThought = (id) => {
    axios
      .delete(`http://localhost:5000/thoughts/${id}`)
      .then(() => setThoughts(thoughts.filter((thought) => thought.id !== id)))
      .catch((error) => console.error('Error removing thought', error));
  };

  return (
    <div className="thought-cabinet">
      <ThoughtList thoughts={thoughts} selectThought={setSelectedThought} removeThought={removeThought} />
      {isAdding && <AddThoughtForm addThought={addThought} />}
      <ThoughtTree thoughts={thoughts} selectThought={setSelectedThought} />
      {selectedThought && (
        <ThoughtDetails
          thought={selectedThought}
          updateThought={addThought} // Используем ту же функцию для обновления
          removeThought={removeThought}
        />
      )}
      <button onClick={() => setIsAdding(true)}>Добавить мысль</button>
    </div>
  );
};

export default ThoughtCabinet;
