// src/components/MiniMap.js
import React from 'react';
import './MiniMap.css';

const MiniMap = ({ thoughts, connections, selectThought }) => {
  return (
    <div className="mini-map">
      <svg className="mini-map-svg">
        {thoughts.map(thought => (
          <circle
            key={thought.id}
            cx={thought.x}
            cy={thought.y}
            r="5"
            fill="white"
            onClick={() => selectThought(thought)}
          />
        ))}
        {connections.map((connection, index) => {
          const fromThought = thoughts.find(thought => thought.id === connection.from);
          const toThought = thoughts.find(thought => thought.id === connection.to);
          if (!fromThought || !toThought) return null;

          return (
            <line
              key={index}
              x1={fromThought.x}
              y1={fromThought.y}
              x2={toThought.x}
              y2={toThought.y}
              stroke="white"
              strokeWidth="1"
            />
          );
        })}
      </svg>
    </div>
  );
};

export default MiniMap;
