// src/components/ThoughtCabinet.js
import React, { useState, useEffect } from 'react';
import ThoughtList from './ThoughtList';
import ThoughtTree from './ThoughtTree';
import ThoughtDetails from './ThoughtDetails';
import AddThoughtForm from './AddThoughtForm';
import ConnectionForm from './ConnectionForm'; 
import MiniMap from './Minimap.js'; // Импортируем MiniMap
import './ThoughtCabinet.css';

const ThoughtCabinet = () => {
  const [thoughts, setThoughts] = useState(() => {
    const savedThoughts = localStorage.getItem('thoughts');
    return savedThoughts ? JSON.parse(savedThoughts) : [
      { id: 1, title: 'Гнилом Лe Миньон', details: 'Детали мысли 1', image: 'path/to/image1.jpg' },
      { id: 2, title: 'Одинокая дорога домой', details: 'Детали мысли 2', image: 'path/to/image2.jpg' },
    ];
  });
  const [connections, setConnections] = useState(() => {
    const savedConnections = localStorage.getItem('connections');
    return savedConnections ? JSON.parse(savedConnections) : [];
  });
  const [selectedThought, setSelectedThought] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [isAdding, setIsAdding] = useState(true);

  useEffect(() => {
    localStorage.setItem('thoughts', JSON.stringify(thoughts));
  }, [thoughts]);

  useEffect(() => {
    localStorage.setItem('connections', JSON.stringify(connections));
  }, [connections]);

  const addThought = (thought) => {
    setThoughts([...thoughts, { ...thought, id: thoughts.length + 1 }]);
    setIsAdding(false);
    setSelectedThought(null);
  };

  const updateThought = (updatedThought) => {
    setThoughts(thoughts.map(thought => thought.id === updatedThought.id ? updatedThought : thought));
    setIsEditing(false);
    setSelectedThought(updatedThought);
  };

  const removeThought = (id) => {
    setThoughts(thoughts.filter(thought => thought.id !== id));
    setSelectedThought(null);
    setIsEditing(false);
    setIsAdding(true);
    setConnections(connections.filter(connection => connection.from !== id && connection.to !== id));
  };

  const selectThought = (thought) => {
    setSelectedThought(thought);
    setIsEditing(false);
    setIsAdding(false);
  };

  const startEditing = () => {
    setIsEditing(true);
    setIsAdding(false);
  };

  const cancelEditing = () => {
    setIsEditing(false);
  };

  const startAdding = () => {
    setIsAdding(true);
    setSelectedThought(null);
    setIsEditing(false);
  };

  const addConnection = (fromId, toId) => {
    if (connections.some(connection => (connection.from === fromId && connection.to === toId) || (connection.from === toId && connection.to === fromId))) {
      return; // Связь уже существует
    }
    setConnections([...connections, { from: fromId, to: toId }]);
  };

  const removeConnection = (fromId, toId) => {
    setConnections(connections.filter(connection => connection.from !== fromId || connection.to !== toId));
  };

  const getRelatedThoughts = (thoughtId) => {
    return connections
      .filter(connection => connection.from === thoughtId || connection.to === thoughtId)
      .map(connection => {
        const relatedId = connection.from === thoughtId ? connection.to : connection.from;
        return thoughts.find(thought => thought.id === relatedId);
      })
      .filter(Boolean);
  };

  return (
    <div className="thought-cabinet">
      <div className="thought-list-container">
        <ThoughtList thoughts={thoughts} selectThought={selectThought} removeThought={removeThought} />
      </div>
      <div className="thought-tree-container">
        <ThoughtTree thoughts={thoughts} connections={connections} selectThought={selectThought} />
      </div>
      <div className="thought-details-container">
        {isEditing ? (
          <ThoughtDetails
            thought={selectedThought}
            updateThought={updateThought}
            cancelEditing={cancelEditing}
            relatedThoughts={getRelatedThoughts(selectedThought.id)}
          />
        ) : isAdding ? (
          <AddThoughtForm addThought={addThought} />
        ) : (
          selectedThought && (
            <>
              <button onClick={startEditing}>Редактировать</button>
              <div>
                <h2>{selectedThought.title}</h2>
                <p>{selectedThought.details}</p>
              </div>
              <button onClick={startAdding}>Добавить новую мысль</button>
              <ConnectionForm
                thoughts={thoughts}
                selectedThought={selectedThought}
                addConnection={addConnection}
                removeConnection={removeConnection}
              />
              <div className="related-thoughts">
                <h3>Связанные мысли</h3>
                <ul>
                  {getRelatedThoughts(selectedThought.id).map(rt => (
                    <li key={rt.id}>{rt.title}</li>
                  ))}
                </ul>
              </div>
            </>
          )
        )}
        {!isAdding && !isEditing && <button onClick={startAdding}>Добавить новую мысль</button>}
      </div>
      <MiniMap thoughts={thoughts} connections={connections} selectThought={selectThought} />
    </div>
  );
};

export default ThoughtCabinet;
