// src/components/AddThoughtForm.js
import React, { useState } from 'react';
import './AddThoughtForm.css';

const AddThoughtForm = ({ addThought }) => {
  const [newThought, setNewThought] = useState({ title: '', details: '', image: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewThought({ ...newThought, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addThought(newThought);
    setNewThought({ title: '', details: '', image: '' });
  };

  return (
    <div className="add-thought-form">
      <h2>Добавить новую мысль</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="title" 
          value={newThought.title} 
          onChange={handleChange} 
          placeholder="Название мысли" 
        />
        <textarea 
          name="details" 
          value={newThought.details} 
          onChange={handleChange} 
          placeholder="Детали мысли"
        />
        <input 
          type="text" 
          name="image" 
          value={newThought.image} 
          onChange={handleChange} 
          placeholder="URL изображения"
        />
        <button type="submit">Добавить</button>
      </form>
    </div>
  );
};

export default AddThoughtForm;
